<?php

namespace SeguridadBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SegPermisoDocumentoRepository extends EntityRepository {
    
    public function permiso($arUsuario, $codigoDocumento, $tipo) {        
        $em = $this->getEntityManager();
        $boolPermiso = false;
        $arPermisoDocumento = new SeguridadBundle\Entity\SegPermisoDocumento();
        $arPermisoDocumento = $em->getRepository('SeguridadBundle:SegPermisoDocumento')->findOneBy(array('codigoUsuarioFk' => $arUsuario->getId(), 'codigoDocumentoFk' => $codigoDocumento));
        if(count($arPermisoDocumento) > 0) {
            switch ($tipo) {
                case 1: //Ingreso
                    if($arPermisoDocumento->getIngreso() == 1) {
                        $boolPermiso = true;
                    }
                    break;
                case 2: //Nuevo
                    if($arPermisoDocumento->getNuevo() == 1) {
                        $boolPermiso = true;
                    }
                    break;
                case 3: //Editar
                    if($arPermisoDocumento->getEditar() == 1) {
                        $boolPermiso = true;
                    }
                    break;    
                case 4: //Eliminar
                    if($arPermisoDocumento->getEliminar() == 1) {
                        $boolPermiso = true;
                    }
                    break;
                case 5: //Autorizar
                    if($arPermisoDocumento->getAutorizar() == 1) {
                        $boolPermiso = true;
                    }
                    break;
                case 6: //Desa-Autorizar
                    if($arPermisoDocumento->getDesautorizar() == 1) {
                        $boolPermiso = true;
                    }
                    break;                    
                case 9: //Anular
                    if($arPermisoDocumento->getAnular() == 1) {
                        $boolPermiso = true;
                    }
                    break;
                case 10: //Imprimir
                    if($arPermisoDocumento->getImprimir() == 1) {
                        $boolPermiso = true;
                    }
                    break;    
            }     
        }                                           
        return $boolPermiso;        
    }    
}